import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-counter',
  standalone: true,
  imports: [
    FormsModule, CommonModule
  ],
  templateUrl: './counter.component.html',
  styleUrl: './counter.component.scss'
})
export class CounterComponent {
  @Input() counter: number = 26;
  errorMessage: string = '';

  increaseCounter() {
    this.errorMessage = '';
    if (this.counter < 0){
      this.errorMessage = 'Nem adható meg ilyen szám';
      this.counter = 0;
    }
    this.counter++;
  }

  decreaseCounter() {
    if (this.counter > 0) {
      this.counter--;
    }
    else {
      this.errorMessage = 'A szám nem felel meg a követelményeknek! Nem lehet negatív.';
    }
  }
}
<div class="d-flex justify-content-center pt-4">
  <div class="card p-4">
    <h1 class="text-center"> Counter: {{counter}} </h1>
    <div class="text-red">{{errorMessage}}</div>
    <div class="d-flex justify-content-between">
      <button (click)="increaseCounter()" class="btn btn-primary me-2">
        Increase +
      </button>

      <button (click)="decreaseCounter()" class="btn btn-secondary me-2">
        Decrease -
      </button>

      <div>
        <input class="form-control" type="text" [(ngModel)]="counter">
      </div>
    </div>
  </div>
</div>
