import {Component, OnInit} from '@angular/core';
import { User } from '../classes/User';
import {UserService} from "./services/user.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrl: './users.component.scss'
})
export class UsersComponent implements OnInit{
  users: User[];

  constructor(private userService: UserService, private router:Router) {
  }

  ngOnInit() {
    this.getUsers()
  }

  private getUsers() {
    this.userService.getUsers().subscribe({
      next: (users)=> {
        this.users = users
        console.log(this.users)
      }
    })
  }

  navigateToCreateUser() {
    this.router.navigate(['user-create']);
  }


  isCardView = true;

  toggleView() {
    this.isCardView = !this.isCardView;
  }
}
<div class="p-2">
    <button (click)="navigateToCreateUser()" class="btn btn-primary">Create</button>
    <button (click)="toggleView()" class="btn btn-danger m-2">
      <svg xmlns="http://www.w3.org/2000/svg"  viewBox="0 0 50 50" width="10px" height="10px"><path d="M 20 4 C 15.054688 4 11 8.054688 11 13 L 11 35.5625 L 5.71875 30.28125 L 4.28125 31.71875 L 11.28125 38.71875 L 12 39.40625 L 12.71875 38.71875 L 19.71875 31.71875 L 18.28125 30.28125 L 13 35.5625 L 13 13 C 13 9.144531 16.144531 6 20 6 L 31 6 L 31 4 Z M 38 10.59375 L 37.28125 11.28125 L 30.28125 18.28125 L 31.71875 19.71875 L 37 14.4375 L 37 37 C 37 40.855469 33.855469 44 30 44 L 19 44 L 19 46 L 30 46 C 34.945313 46 39 41.945313 39 37 L 39 14.4375 L 44.28125 19.71875 L 45.71875 18.28125 L 38.71875 11.28125 Z"/></svg>
      {{ isCardView ? 'Switch to Table View' : 'Switch to Card View' }}
    </button>
</div>

<div>
</div>

<div *ngIf="isCardView">
  <app-card-list-view [users]="users"></app-card-list-view>
</div>

<div *ngIf="!isCardView">
  <app-table-list-view [users]="users"></app-table-list-view>
</div>
